// Generate script prompt for Gemini based on video type and topic

const spaInfo = {
  name: "Oasis Luxury Med Spa",
  tagline: "Personalized care meets advanced aesthetics",
  phone: "832-295-3086",
  website: "oasisluxurymedspa.com",
  address: "7930 Broadway Street Suite 116, Pearland, TX",
  hours: "Mon-Fri 9am-6:30pm, Sat 9am-3pm, Sun 10am-1pm",
  financing: "CareCredit financing available",
  services: "Botox, Fillers, Laser Treatments, Microneedling, Body Contouring, Hair Removal, Hair Restoration, Weight Loss, Massage, Professional Skincare",
  specialties: "Fotona 4D Facelift, SmoothEye Laser, SkinPen Microneedling, Salmon Sperm DNA Treatment",
  philosophy: "Upscale, results-driven aesthetic treatments with personalized care in a luxurious environment"
};

const character = $json.character;
const videoType = $json.videoType;
const topic = $json.topic;
const customMessage = $json.customMessage;
const durationSec = $json.durationMs / 1000;

// If custom message provided, use it
if (customMessage && customMessage.trim().length > 0) {
  return [{
    json: {
      ...$json,
      scriptText: customMessage.trim(),
      spaInfo: spaInfo
    }
  }];
}

// Build script generation prompt
let scriptPrompt = "";

if (videoType.includes("Call to Action")) {
  scriptPrompt = `Write a ${durationSec}-second script for ${character.name}, a ${character.role} at Oasis Luxury Med Spa, promoting their services.

Topic focus: ${topic}

Med Spa info to incorporate:
- Name: ${spaInfo.name}
- Phone: ${spaInfo.phone}
- Website: ${spaInfo.website}
- Location: Pearland, TX
- ${spaInfo.financing}

Requirements:
- The presenter is speaking directly to potential clients
- Include a clear call to action (book a consultation, call us, visit website)
- Keep it professional yet warm and approachable
- Speak with confidence and expertise
- Make the viewer feel they deserve this luxury treatment
- MUST be speakable in ${durationSec} seconds (about ${Math.round(durationSec * 2.5)} words max)
- End with contact info naturally woven in

Only output the script text, nothing else.`;
} else if (videoType.includes("Tips")) {
  scriptPrompt = `Write a ${durationSec}-second educational script for ${character.name}, a ${character.role} at Oasis Luxury Med Spa, sharing expert skincare/wellness tips.

Topic focus: ${topic}

Med Spa info to incorporate:
- Name: ${spaInfo.name}
- Specialties: ${spaInfo.specialties}
- Philosophy: ${spaInfo.philosophy}

Requirements:
- The presenter is sharing a professional tip or insider knowledge
- Educational but engaging — position them as the trusted expert
- Keep it polished and authoritative yet friendly
- Make viewers feel like they're getting exclusive advice
- MUST be speakable in ${durationSec} seconds (about ${Math.round(durationSec * 2.5)} words max)
- Mention Oasis Luxury Med Spa naturally

Only output the script text, nothing else.`;
} else if (videoType.includes("Before & After")) {
  scriptPrompt = `Write a ${durationSec}-second transformation/results script for ${character.name}, a ${character.role} at Oasis Luxury Med Spa.

Topic focus: ${topic}

Med Spa info to incorporate:
- Name: ${spaInfo.name}
- Specialties: ${spaInfo.specialties}

Requirements:
- The presenter is highlighting amazing results clients can expect
- Build excitement about transformations without making medical claims
- Use phrases like "our clients love" and "you can see the difference"
- Keep it professional, aspirational, and compliant
- MUST be speakable in ${durationSec} seconds (about ${Math.round(durationSec * 2.5)} words max)
- End with an invitation to see their own transformation

Only output the script text, nothing else.`;
} else {
  // Service Spotlight
  scriptPrompt = `Write a ${durationSec}-second service spotlight script for ${character.name}, a ${character.role} at Oasis Luxury Med Spa.

Service to spotlight: ${topic}

Med Spa info to incorporate:
- Name: ${spaInfo.name}
- Phone: ${spaInfo.phone}
- Website: ${spaInfo.website}
- ${spaInfo.financing}

Requirements:
- The presenter is introducing and explaining a specific service
- Highlight key benefits and what makes this treatment special
- Address common concerns or questions about the treatment
- Keep it informative, professional, and inviting
- MUST be speakable in ${durationSec} seconds (about ${Math.round(durationSec * 2.5)} words max)
- Include a soft call to action

Only output the script text, nothing else.`;
}

return [{
  json: {
    ...$json,
    scriptPrompt: scriptPrompt,
    spaInfo: spaInfo
  }
}];

// Extract script from Gemini response
const geminiResponse = $json;

let scriptText = "";

// Handle case where custom message was provided (scriptText already exists)
if ($('Build Script Prompt').item.json.customMessage && $('Build Script Prompt').item.json.customMessage.trim().length > 0) {
  scriptText = $('Build Script Prompt').item.json.customMessage.trim();
} else {
  // Extract from Gemini response
  try {
    scriptText = geminiResponse.candidates[0].content.parts[0].text.trim();
  } catch (e) {
    throw new Error("Failed to extract script from Gemini response: " + JSON.stringify(geminiResponse));
  }
}

// Clean up the script - remove quotes if wrapped
scriptText = scriptText.replace(/^"|"$/g, '').trim();

return [{
  json: {
    ...$('Build Script Prompt').item.json,
    scriptText: scriptText
  }
}];

// Extract image from Gemini response and convert to binary
const geminiResponse = $json;
const prevData = $('Extract Script Text').item.json;

let imageBase64 = "";
let mimeType = "image/png";

try {
  // Gemini returns image in candidates[0].content.parts - find the image part
  const parts = geminiResponse.candidates[0].content.parts;
  for (const part of parts) {
    if (part.inlineData) {
      imageBase64 = part.inlineData.data;
      mimeType = part.inlineData.mimeType || "image/png";
      break;
    }
  }

  if (!imageBase64) {
    throw new Error("No image data found in Gemini response");
  }
} catch (e) {
  throw new Error("Failed to extract image from Gemini response: " + e.message + " Response: " + JSON.stringify(geminiResponse).substring(0, 500));
}

// Return with binary data
const binaryData = Buffer.from(imageBase64, 'base64');

return [{
  json: {
    ...prevData,
    imageMimeType: mimeType
  },
  binary: {
    image: {
      data: imageBase64,
      mimeType: mimeType,
      fileName: `avatar_${prevData.character.name.toLowerCase()}.png`
    }
  }
}];

// Normalize ElevenLabs MP3 binary output to binary.audio
// and preserve the image binary

const prevData = $('Extract Image to Binary').item.json;
const prevBinary = $('Extract Image to Binary').item.binary;

if (!$binary || Object.keys($binary).length === 0) {
  throw new Error("No binary from ElevenLabs. Check your ElevenLabs credential and ensure Response Format = File.");
}

// ElevenLabs returns binary under 'data'
const audioSrc = $binary.data ? "data" : Object.keys($binary)[0];

return [{
  json: prevData,
  binary: {
    image: prevBinary.image,
    audio: $binary[audioSrc]
  }
}];

// Merge results from both upload branches and prepare for generation
const imageAssetId = $('Hedra - Create Image Asset').item.json.id;
const audioAssetId = $('Hedra - Create Audio Asset').item.json.id;
const originalData = $('Normalize Audio Binary').item.json;

return [{
  json: {
    ...originalData,
    imageAssetId: imageAssetId,
    audioAssetId: audioAssetId
  }
}];

// Pick the best Hedra model for realistic video
const models = $json;
const prevData = $('Merge Asset IDs').item.json;

if (!Array.isArray(models) || models.length === 0) {
  throw new Error("No models returned from Hedra. Check your Hedra API credential.");
}

// Try to find a model that supports video, prefer newer/better ones
let selectedModel = models[0];
for (const model of models) {
  if (model.name && model.name.toLowerCase().includes('character')) {
    selectedModel = model;
    break;
  }
}

return [{
  json: {
    ...prevData,
    modelId: selectedModel.id,
    modelName: selectedModel.name || 'default'
  }
}];

// Store generation ID and original data for polling
const generationId = $json.id;
const prevData = $('Pick Hedra Model').item.json;

return [{
  json: {
    ...prevData,
    generationId: generationId
  }
}];

// Check status and preserve all data
const status = $json.status;
const downloadUrl = $json.download_url || $json.url || null;
const errorMessage = $json.error_message || null;
const prevData = $('Store Generation ID').item.json;

return [{
  json: {
    ...prevData,
    status: status,
    downloadUrl: downloadUrl,
    errorMessage: errorMessage
  }
}];

// Throw error with message from Hedra
const errorMsg = $json.errorMessage || "Unknown Hedra generation error";
throw new Error("Hedra Error: " + errorMsg);

// Normalize video binary for Google Drive upload
const prevData = $('Check Status').item.json;

if (!$binary || Object.keys($binary).length === 0) {
  throw new Error("No video binary found. Check that Download Video has Response Format = File.");
}

const src = $binary.data ? "data" : Object.keys($binary)[0];

// Generate filename with character name and timestamp
const timestamp = new Date().toISOString().replace(/[:.]/g, '-').substring(0, 19);
const filename = `oasis_medspa_${prevData.character.name.toLowerCase()}_${timestamp}.mp4`;

return [{
  json: {
    ...prevData,
    filename: filename
  },
  binary: {
    video: {
      ...$binary[src],
      fileName: filename
    }
  }
}];

// Prepare data for Google Sheets logging
const driveResponse = $json;
const prevData = $('Normalize Video Binary').item.json;

// Build the Drive file URL
const fileId = driveResponse.id;
const driveUrl = `https://drive.google.com/file/d/${fileId}/view`;

return [{
  json: {
    timestamp: prevData.timestamp,
    characterName: prevData.character.name,
    characterRole: prevData.character.role,
    characterEthnicity: prevData.character.ethnicity,
    videoType: prevData.videoType,
    topic: prevData.topic,
    scriptText: prevData.scriptText,
    durationSeconds: prevData.durationMs / 1000,
    backgroundStyle: prevData.backgroundStyle,
    filename: prevData.filename,
    driveFileId: fileId,
    driveUrl: driveUrl,
    status: "Success"
  }
}];

